import Card from "../UI/Card";
import classes from "./ProductItem.module.css";
import { useDispatch, useSelector } from "react-redux/es/exports";
import { addActions } from "../../store";
const ProductItem = (props) => {
  const { title, price, description } = props;
  const allProducts = props.productsArray;
  const cartItems = useSelector((state) => state.add.items);
  const itemQuantity = useSelector((state) => state.add.quantity);
  const dispatch = useDispatch();

  const addItemHandler = (event) => {
    const [item] = allProducts.filter((ele) => {
      return ele.id === event.target.id;
    });
    const finalItem = { ...item, quantity: itemQuantity };
    console.log(finalItem);
    // console.log(cartItems, "kkk");
    if (cartItems.indexOf(finalItem)) {
      console.log("exist");
    }
    dispatch(addActions.addItem({ value: finalItem }));
    console.log(cartItems, "mmm");
    dispatch(addActions.addQuantity());
    dispatch(addActions.addTotalQuantity());
  };
  return (
    <li className={classes.item}>
      <Card>
        <header>
          <h3>{title}</h3>
          <div className={classes.price}>${price.toFixed(2)}</div>
        </header>
        <p>{description}</p>
        <div className={classes.actions}>
          <button onClick={addItemHandler} id={props.id}>
            Add to Cart
          </button>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
