import Card from "../UI/Card";
import classes from "./Cart.module.css";
import CartItem from "./CartItem";
import { useSelector, useDispatch } from "react-redux";
import useHttp from "../../hooks/use-http";
import { useEffect, useState } from "react";
import { manageCartActions } from "../../store/manageCartSlice";
const Cart = (props) => {
  const cartItems = useSelector((state) => {
    return state.add.items;
  });

  /////////////////////
  const dispatch = useDispatch();
  //
  // replaceCart(state, action) {
  //   // state.totalQuantity = action.payload.totalQuantity;
  //   // state.items = action.payload.items;
  // },
  //
  const { isLoading, error, requestFn: getData } = useHttp();
  const [array, setArray] = useState([]);
  useEffect(() => {
    const submitFn = (data) => {
      // setShowNotification(true);
      // const loadedItems = [];
      // Object.entries(data).map(([key, value]) => {
      //   loadedItems.push({
      //     // id: value.id,
      //     id: key,
      //     // name: value.name,
      //     // desc: value.description,
      //     // price: value.price,
      //     items: value.items,
      //   });
      // });
      // setCartItems(loadedItems);
      dispatch(
        manageCartActions.replaceCart({
          items: data.items,
          totalQuantity: data.TotalQuantity,
        })
      );
      // setArray(data);
      // console.log(data.TotalQuantity, "dddddd");
    };
    getData(
      {
        url: "https://food-e7d7c-default-rtdb.firebaseio.com/cart.json",
      },
      submitFn
    );
  }, [getData, dispatch]);
  console.log(cartItems, "kkk");
  /////////////////

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>
        {cartItems.map((ele) => {
          return (
            <CartItem
              key={ele.id}
              id={ele.id}
              title={ele.title}
              quantity={ele.quantity}
              price={ele.price}
            />
          );
        })}
      </ul>
    </Card>
  );
};

export default Cart;
