import { createSlice, configureStore } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "show",
  initialState: { show: false },
  reducers: {
    showCart(state, action) {
      state.show = !state.show;
    },
  },
});
const addSlice = createSlice({
  name: "Add",
  initialState: { items: [], TotalQuantity: 0, totalPrice: 0, amount: 1 },
  reducers: {
    addItem(state, action) {
      state.items = [...state.items, action.value];
      state.TotalQuantity = state.TotalQuantity + 1;
      state.totalPrice = state.totalPrice + action.payload.price;
      state.quantity = state.quantity + state.payload.quantity;
    },
  },
});

const store = configureStore({
  reducer: { cart: cartSlice.reducer, add: addSlice.reducer },
});

export const showCartActions = cartSlice.actions;
export const addActions = addSlice.actions;
export default store;
