import { createSlice, configureStore } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "show",
  initialState: { show: false },
  reducers: {
    showCart(state, action) {
      state.show = !state.show;
    },
  },
});
const addSlice = createSlice({
  name: "Add",
  initialState: { items: [], TotalQuantity: 0, totalPrice: 0, quantity: 0 },
  reducers: {
    addItem(state, action) {
      const newItem = action.payload.value;
      if (state.items.find((item) => item.id === newItem.id)) {
        console.log("ggggggg");
      } else {
        state.items = [...state.items, newItem];
      }
    },
    addQuantity(state, action) {
      state.quantity = state.quantity + 1;
    },
    addTotalQuantity(state, action) {
      state.TotalQuantity = state.TotalQuantity + 1;
    },
    addTotalPrice(state, action) {
      state.totalPrice = state.totalPrice + action.payload.price;
    },
  },
});

const store = configureStore({
  reducer: { cart: cartSlice.reducer, add: addSlice.reducer },
});

export const showCartActions = cartSlice.actions;
export const addActions = addSlice.actions;
export default store;
