import { createSlice } from "@reduxjs/toolkit";

const addSlice = createSlice({
  name: "Add",
  initialState: { items: [], TotalQuantity: 0 },
  reducers: {
    addItem(state, action) {
      const newItem = { ...action.payload.value, quantity: 1 };
      const existingItem = state.items.find((item) => item.id === newItem.id);
      if (existingItem) {
        const upatedItem = {
          ...existingItem,
          quantity: existingItem.quantity + 1,
        };
        state.items[state.items.indexOf(existingItem)] = upatedItem;
        const updateItems = state.items;
        state.items = updateItems;
      } else {
        state.items = [...state.items, newItem];
      }
    },

    addTotalQuantity(state, action) {
      state.TotalQuantity = state.TotalQuantity + 1;
    },
    removeTotalQuantity(state, action) {
      state.TotalQuantity = state.TotalQuantity - 1;
    },

    removeItem(state, action) {
      const removeItem = { ...action.payload.value };
      const existingItem = state.items.find(
        (item) => item.id === removeItem.id
      );
      const upatedItem = {
        ...existingItem,
        quantity: existingItem.quantity - 1,
      };
      state.items[state.items.indexOf(existingItem)] = upatedItem;
      let updateItems;
      if (existingItem.quantity === 1) {
        updateItems = state.items.filter((ele) => {
          return ele.id !== existingItem.id;
        });
      } else {
        updateItems = state.items;
      }
      state.items = updateItems;
    },
  },
});

export const addActions = addSlice.actions;
export default addSlice;
